#!/bin/python
# coding: utf-8

##########################################################################################################################################

from pathlib import Path

from jsoncomment import JsonComment

##########################################################################################################################################

MAIN_PY = Path(__file__)
MAIN_JFC = MAIN_PY.with_suffix(".jsonc")
MAIN_JFS = MAIN_PY.with_suffix(".json")

##########################################################################################################################################

def main():
	json = JsonComment()

	json_objc = json.loadf(MAIN_JFC)

	json.dumpf(json_objc, MAIN_JFS)

if __name__ == "__main__":
	main()

##########################################################################################################################################
