class Box:
    SINGLE: "Box" = None
    DOUBLE: "Box" = None
    DOTTED: "Box" = None
    BOLD: "Box" = None
    BLANK: "Box" = None
    ASCII: "Box" = None
    CURVED: "Box" = None
    
    
    def __init__( self, x ):
        self.__in = x
        self.ts, self.bs, self.ls, self.rs, self.tl, self.tr, self.bl, self.br = x
        self.hr = self.ts
        self.vr = self.ls
        self.em = " "
    
    
    def format( self, prefix, suffix ):
        return Box( "".join( "{}{}{}".format( prefix, char, suffix ) for char in self.__in ) )


Box.SINGLE = Box( "──││┌┐└┘" )
